import {useSelector} from "react-redux"
import React, { useEffect , useState} from "react";
import axios from "axios"
import MainMenu from "../../components/MainMenu";
import "./myBookings.css"
import moment from 'moment'

function MyBookings({match}) {

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const [address, setAddress] = useState("");
    const [floor, setFloor] = useState("");
    const [date, setDate] = useState("");
    const [desk, setDesk] = useState("");
    const [noFloors, setFloors] = useState("");
    const [fetchedDataActie, setFetchedDataActive] = useState([]);
    const [fetchedDataExpired, setFetchedDataExpired] = useState([]);
    useEffect(() => {

        const fetching = async () => {
              
            const config = {
                headers: {
                    Authorization: `Bearer ${userInfo.token}`,
                },
              };

            const response  = await axios.get(`/api/bookings/active/${match.params.user}`, config)
            const respone2 = await axios.get(`/api/bookings/expired/${match.params.user}`, config)
            console.log(response.data)
            setFetchedDataActive(response.data)
            setFetchedDataExpired(respone2.data)
            
          };
      
          fetching();
    },[match.params.user])

    function Filter() {
        return(
            <div class="btn-group" role="group" aria-label="Basic checkbox toggle button group">
            <input type="checkbox" class="btn-check" id="btncheck1" checked="" autocomplete="off"></input>
            <label class="btn btn-primary" for="btncheck1">Checkbox 1</label>
            <input type="checkbox" class="btn-check" id="btncheck2" autocomplete="off"></input>
            <label class="btn btn-primary" for="btncheck2">Checkbox 2</label>
            <input type="checkbox" class="btn-check" id="btncheck3" autocomplete="off"></input>
            <label class="btn btn-primary" for="btncheck3">Checkbox 3</label>
          </div>
        )
    }
      
    return(
        <div>
            <MainMenu uInfo={userInfo}></MainMenu>      
            <div className="content">
                <Filter></Filter>
                <h1>Current Bookings!</h1>
                <table className="table table-hover">
                    <thead>
                        <tr>
                            <th scope="col">Type</th>
                            <th scope="col">Address</th>
                            <th scope="col">Date</th>
                            <th scope="col">Floor</th>
                            <th scope="col">Desk</th>
                        </tr>
                    </thead>
                {fetchedDataActie?.map((b) =>        
                    <tbody>
                        <tr className="table-success">
                        <th scope="row">Active</th>
                        <td>{b.address}</td>
                        <td>{moment(b.date).format('yyyy/MM/DD')}</td>
                        <td>{b.floor}</td>
                        <td>{b.desk}</td>
                        </tr>
                    </tbody>
                )}
                </table>
            <div className="expired">
                <h2>Bookings History:</h2>
                <table className="table table-hover">
                    <thead>
                        <tr>
                            <th scope="col">Type</th>
                            <th scope="col">Address</th>
                            <th scope="col">Date</th>
                            <th scope="col">Floor</th>
                            <th scope="col">Desk</th>
                        </tr>
                    </thead>
                {fetchedDataExpired?.map((b) => 
                    <tbody>
                        <tr className="table-active">
                        <th scope="row">Expired</th>
                        <td>{b.address}</td>
                        <td>{moment(b.date).format('yyyy/MM/DD')}</td>
                        <td>{b.floor}</td>
                        <td>{b.desk}</td>
                        </tr>
                    </tbody>
                )}
                </table>
                
            </div>

            </div>
        </div>
        
    )
}

export default MyBookings;
