import {
    CITIES_LIST_FAIL,
    CITIES_LIST_SUCCESS,
    CITIES_LIST_REQUEST
} from '../constants/cityConstants'


export const cityListReducer = (state = { cities: [] }, action) => {
    switch (action.type) {
      case CITIES_LIST_REQUEST:
        return { loading: true };
      case CITIES_LIST_SUCCESS:
        return { loading: false, cities: action.payload };
      case CITIES_LIST_FAIL:
        return { loading: false, error: action.payload };
  
      default:
        return state;
    }
  };
