import TextBar from "../../components/TextBar";
import MainMenu from "../../components/MainMenu"
import {useSelector} from "react-redux"
import React, { useEffect , useState} from "react";
import axios from "axios"

function Request(){

    
    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;
    const [fetchedAllData, setFetchedAllData] = useState([]);

    useEffect(() => {

        const fetching = async () => {
              
            const config = {
                headers: {
                    Authorization: `Bearer ${userInfo.token}`,
                },
              };
            const respone = await axios.get(`/api/request/`, config);
        
            setFetchedAllData(respone.data)
          };
      
          fetching();
    },[])

    function ManageRequests(){
        return(
            <>
            {fetchedAllData?.map((b,i) => {
                
             return( 
                <tr>
                <td>{i+1}</td>
               
                <td><p>Open a PDF file <a href="/uploads/media/default/0001/01/540cb75550adf33f281f29132dddd14fded85bfc.pdf">example</a></p></td>
                <td><span className="badge-success">{b.status}</span></td>
                
                
                <td><button className="btn btn-danger">Delete</button></td>
                </tr>
                )})}
        </>      
        )
    }
    return(
    <>
    <MainMenu uInfo={userInfo}></MainMenu>
    <TextBar text={"Request"} subText={"Check and validate vaccination certificates"}></TextBar>
    <div class="container-xl" >    
        <div class="table-responsive">
            <div class="table-wrapper">   
            <div class="table-title">
                <div class="row">
                    <div class="col-sm-6"><h2>Manage <b>Requests</b></h2></div>
                    <div class="col-sm-6">
                        <div class="btn-group" data-toggle="buttons">
        
                        </div>
                    </div>
                </div>
            </div>
            <table class="table table-striped table-hover">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>DCC</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                 <ManageRequests/>
                </tbody>
            </table>
        </div> 
    </div>   
</div> 
    </>
    )
}


export default Request;
