import {
    CITIES_LIST_FAIL,
    CITIES_LIST_SUCCESS,
    CITIES_LIST_REQUEST
} from '../constants/cityConstants'
import axios from "axios";

export const listCities= () => async (dispatch, getState) => {
    try {
      dispatch({
        type: CITIES_LIST_REQUEST,
      });
  
      const {
        userLogin: { userInfo },
      } = getState();
  
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`,
        },
      };
  
      const { data } = await fetch("https://api/buildings/");
  
      dispatch({
        type: CITIES_LIST_SUCCESS,
        payload: data,
      });
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message;
      dispatch({
        type: CITIES_LIST_FAIL,
        payload: message,
      });
    }
  };
