import React, { useEffect, useState } from "react";
import {
  Container,
  Form,
  FormControl,
  Nav,
  Navbar,
  NavDropdown,
} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import {} from "react-router-dom";
import { logout } from "../actions/userActions"


function Header({ setSearch }) {
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };

  const [isOn, setOn] = React.useState(JSON.parse(localStorage.getItem('is-on')) || false);
  const [checked, setCheck] = React.useState(false)

  const handleToggle = () => {
    setOn(!isOn);
   
  }
  const change = () => {
    if(isOn)
    setCheck("true")
  else
    setCheck("")
  }

  useEffect(() => {
    localStorage.setItem('is-on', JSON.stringify(!isOn));
  }, [userInfo],[isOn]);
 
 
  return (
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
      <Container>
        <Navbar.Brand href="/">Safe Office</Navbar.Brand>

        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="m-auto">
          </Nav>
          <Nav>
            { userInfo ? ( 
              <div className="form-check form-switch">
              <input class="form-check-input" type="checkbox" id="flexSwitchCheckDefault" checked={check}
               onClick={{handleToggle,change}}></input>
                <label className="form-check-label"  style ={{ color:'red'}} for="flexSwitchCheckDefault">Protocol off</label>
                <span>{`Value: ${isOn}`}</span>
            </div>
            ) : <></>}
          </Nav>
          <Nav>
            {userInfo ? (
              <>
                <Nav.Link href="/mybookings"></Nav.Link>
                <NavDropdown
                  title={`${userInfo.name}`}
                  id="collasible-nav-dropdown"
                >
                  <NavDropdown.Item href="/profile">
                    {/* <img
                      alt=""
                      src={`${userInfo.pic}`}
                      width="25"
                      height="25"
                      style={{ marginRight: 10 }}
                    /> */}
                    My Profile
                  </NavDropdown.Item>

                  <NavDropdown.Divider />
                  <NavDropdown.Item onClick={logoutHandler}>
                    Logout
                  </NavDropdown.Item>
                </NavDropdown>
              </>
            ) : (
              <Nav.Link href="/login">Login</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}


export default Header;
