import TextBar from "../../components/TextBar";
import MainMenu from "../../components/MainMenu"
import {useSelector} from "react-redux"
import React, { useEffect , useState} from "react";
import axios from "axios"

function Request(){

    
    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;
    const [fetchedAllData, setFetchedAllData] = useState([]);
    const [showConfirmation, setShowConfirmation] = React.useState(false)

    useEffect(() => {

        const fetching = async () => {
              
            const config = {
                headers: {
                    Authorization: `Bearer ${userInfo.token}`,
                },
              };
            const respone = await axios.get(`/api/request/`, config);
        
            setFetchedAllData(respone.data)
          };
      
          fetching();
    },[])

    function Confirmation(props){
        return(
          <div class="card bg-secondary mb-3" id="confirmation">
            <div class="card-body">
            <iframe id="frame" src={props.pdf}></iframe>
            </div>
            <div class="modal-footer">
                <button onClick={ () => {setShowConfirmation(false);}} type="button" class="btn btn-danger"  data-bs-dismiss="modal">Close</button>
              </div>
          </div>
          )}

    function ManageRequests(){
        return(
            <>
            {fetchedAllData?.map((b,i) => {
                
             return( 
                <tr>
                <td>{i+1}</td>
                <td>Nume</td>
                <td><a onClick={() => {setShowConfirmation(true)}}>Click to view</a></td>  
                {showConfirmation ? <Confirmation pdf={b.dcc} /> : null}
                <td><button className="btn btn-danger">Delete</button></td>
                </tr>
                )})}
        </>      
        )
    }
    return(
    <>
    <MainMenu uInfo={userInfo}></MainMenu>
    <TextBar text={"Request"} subText={"Check and validate vaccination certificates"}></TextBar>
    <div class="container-xl" >    
        <div class="table-responsive">
            <div class="table-wrapper">   
            <div class="table-title">
                <div class="row">
                    <div class="col-sm-6"><h2>Manage <b>Requests</b></h2></div>
                    <div class="col-sm-6">
                        <div class="btn-group" data-toggle="buttons">
        
                        </div>
                    </div>
                </div>
            </div>
            <table class="table table-striped table-hover">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>DCC</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                 <ManageRequests/>
                </tbody>
            </table>
        </div> 
    </div>   
</div> 
    </>
    )
}


export default Request;
